// src/components/LevelSelector.jsx
import React from 'react';
import { useNavigate } from 'react-router-dom';
import { getUser, logoutUser, getProgress, getResult } from '../utils/userStorage';
import '../styles/level-selector.css';

const LevelSelector = () => {
  const navigate = useNavigate();
  const user = getUser();
  const progress = getProgress();

  const easy = getResult(user.username, 'easy');
  const medium = getResult(user.username, 'medium');
  const hard = getResult(user.username, 'hard');

  const handleSelect = (level) => {
    navigate(`/game/${level}`);
  };

  const handleLogout = () => {
    logoutUser();
    navigate('/');
  };

  return (
    <div className={`level-selector ${user?.gender === 'female' ? 'female-theme' : 'male-theme'}`}>
      <header className="level-header">
        <h2>ŸÖÿ±ÿ≠ÿ®Ÿãÿß {user?.username} üëã</h2>
        <p>ÿßŸÑÿπŸÖÿ±: {user?.age} | ÿßŸÑÿ¨ŸÜÿ≥: {user?.gender === 'female' ? 'ÿ£ŸÜÿ´Ÿâ' : 'ÿ∞ŸÉÿ±'}</p>
        <p>üìä ÿ™ŸÇÿØŸÖŸÉ ÿßŸÑÿ≥ÿßÿ®ŸÇ: {progress} ŸÜŸÇÿ∑ÿ©</p>
        <button className="logout-button" onClick={handleLogout}>ÿ™ÿ≥ÿ¨ŸäŸÑ ÿßŸÑÿÆÿ±Ÿàÿ¨</button>
      </header>

      <div className="results-summary">
        <h4>ŸÜÿ™ÿßÿ¶ÿ¨ŸÉ ÿßŸÑÿ≥ÿßÿ®ŸÇÿ©:</h4>
        <p>ÿ≥ŸáŸÑ: {easy ? `${easy.score}/${easy.total} (${Math.round((easy.score / easy.total) * 100)}%)` : 'ŸÑÿß ŸäŸàÿ¨ÿØ'}</p>
        <p>ŸÖÿ™Ÿàÿ≥ÿ∑: {medium ? `${medium.score}/${medium.total} (${Math.round((medium.score / medium.total) * 100)}%)` : 'ŸÑÿß ŸäŸàÿ¨ÿØ'}</p>
        <p>ŸÖÿ≠ÿ™ÿ±ŸÅ: {hard ? `${hard.score}/${hard.total} (${Math.round((hard.score / hard.total) * 100)}%)` : 'ŸÑÿß ŸäŸàÿ¨ÿØ'}</p>
      </div>

      <h3>ÿßÿÆÿ™ÿ± ÿßŸÑŸÖÿ≥ÿ™ŸàŸâ</h3>
      <div className="levels">
        <button onClick={() => handleSelect('easy')}>ÿ≥ŸáŸÑ</button>
        <button onClick={() => handleSelect('medium')}>ŸÖÿ™Ÿàÿ≥ÿ∑</button>
        <button onClick={() => handleSelect('hard')}>ŸÖÿ≠ÿ™ÿ±ŸÅ</button>
      </div>
    </div>
  );
};

export default LevelSelector;
