// توليد أسئلة حسب المستوى

export const generateQuestions = (level) => {
  const questions = [];

  for (let i = 0; i < 20; i++) {
    let question = {};
    switch (level) {
      case 'easy':
        question = generateEasyQuestion();
        break;
      case 'medium':
        question = generateMediumQuestion();
        break;
      case 'hard':
        question = generateHardQuestion();
        break;
      default:
        break;
    }
    questions.push(question);
  }

  return questions;
};

// مثال على توليد سؤال سهل
const generateEasyQuestion = () => {
  const a = Math.floor(Math.random() * 9) + 1;
  const b = Math.floor(Math.random() * 9) + 1;
  const op = Math.random() > 0.5 ? '+' : '-';
  const answer = op === '+' ? a + b : a - b;

  return {
    question: `${a} ${op} ${b}`,
    answer,
    type: Math.random() > 0.5 ? 'multiple' : 'input'
  };
};

